<?php function imageResize($image, $target, $alt=NULL) {//Gets the width and height of the image and outputs it as $theimage (width) and $theimage (height)$theimage = getimagesize($image);$width = $theimage[0];$height = $theimage[1];//takes the larger size of the width and height and applies the formula accordingly...this is so this script will work dynamically with any size imageif ($width > $height) {$percentage = ($target / $width);} else {$percentage = ($target / $height);}//gets the new value and applies the percentage, then rounds the value$width = round($width * $percentage);$height = round($height * $percentage);//Returns the new sizes inside an image tag so you can call it with echo "<img src=\"$image\" width=\"$width\" height=\"$height\" alt=\"$alt\" align=center>";}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head><title>Sakai Project: SEPP Conference Facebook</title><meta http-equiv="Content-Type" content="text/html; charset=iso-8859-1"><meta name="description" content=",Sakai - www.sakaiproject.org, Community Source Software for Education."><meta name="keywords" content=",sakai, sakaiproject.org, sakaiproject, Sakai, Community Source, Open Source, open-open, educational software, collaborative"><meta name="robots" content="index, follow"><link href="template_css.css" rel="stylesheet" type="text/css"><link rel="stylesheet" href="facebook2.css" type="text/css"/></head><body><table bgcolor="#ffffff" border="0" cellpadding="0" cellspacing="0" width="100%">  <tbody>    <tr>      <td class="mainHeader" width="100%"><table width="100%" border="0" cellpadding="0" cellspacing="0">        <div id="photo">  <?php	require('facebook_db.php');	$query  = "SELECT * FROM conference_album";$searchresult = mysql_query($query);// print the images		while($links=mysql_fetch_array($searchresult))		{		